mapper.insert:
	insert into baseballboard values
 	((select nvl(max(seq),0)+1 from baseballboard)
	,#{id}
	,#{nickname}
	,#{title}
	,#{content}
	,sysdate
	,#{team}
	,#{person}
	,#{local}
	,''
	,#{sports}
	,0
	,'')

	insert into footballboard values
